.backdrop {
  position: fixed;
  left: 0;
  top: 0;
  width: 100vw;
  height: 100vh;
  z-index: 6;
  background-color: rgba(0, 0, 0, 0.4);
}
.backdrop.is-hidden {
  opacity: 0;
  pointer-events: none;
  visibility: none;
}
.backdrop.is-hidden .modal {
  transform: scale(0.2);
}

.modal {
  position: absolute;
  padding: 40px;
  width: 80vw;
  min-height: 100vh;
  top: 1%;
  left: 50%;
  transform: translate(-50%, -1%) scale(1);
  background-color: var(--background-color);
  border-radius: 70px;
}
@media screen and (min-width: 480px) {
  .modal {
    width: 420px;
    min-height: 735px;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%) scale(1);
  }
}
@media screen and (min-width: 768px) {
  .modal {
    width: 480px;
  }
}
@media screen and (min-width: 1200px) {
  .modal {
    min-height: 735px;
  }
}
.modal__form {
  display: flex;
  flex-direction: column;
}
.modal__call {
  text-align: center;
  margin-bottom: 12px;
  font-size: 30px;
  font-weight: 700;
  line-height: 1.5;
  color: var(--main-white-color);
}
@media screen and (min-width: 1200px) {
  .modal__call {
    font-size: 30px;
    font-weight: 700;
    line-height: 1.15;
  }
}
.modal__name-label {
  margin-bottom: 4px;
  color: var(--main-white-color);
  font-size: 15px;
  line-height: 1.2;
  letter-spacing: 0.01em;
}
.modal__inputs {
  padding-left: 20px;
  height: 40px;
  width: 100%;
  border: 2px solid rgba(33, 33, 33, 0.2);
  border-radius: 25px;
  outline: none;
}
.modal__inputs:focus, .modal__inputs:active {
  border: 2px solid var(--main-accent-color);
}
.modal__input-containers {
  position: relative;
  margin-bottom: 15px;
}
.modal__input-count {
  text-align: center;
  height: 40px;
  width: 100%;
  border: 2px solid rgba(33, 33, 33, 0.2);
  border-radius: 25px;
  outline: none;
}
.modal__input-count:focus, .modal__input-count:active {
  border: 2px solid var(--main-accent-color);
}
.modal__select {
  padding-left: 20px;
  height: 40px;
  width: 100%;
  border: 2px solid rgba(33, 33, 33, 0.2);
  border-radius: 25px;
  outline: none;
}
.modal__select:focus, .modal__select:active {
  border: 2px solid var(--main-accent-color);
}
.modal__textarea {
  margin-bottom: 30px;
  padding: 12px 16px;
  height: 120px;
  font-size: 12px;
  line-height: 1.17;
  letter-spacing: 0.01em;
  border: 2px solid rgba(33, 33, 33, 0.2);
  border-radius: 25px;
  resize: none;
  outline: none;
}
@media screen and (min-width: 1200px) {
  .modal__textarea {
    font-size: 14px;
    line-height: 1.14;
    letter-spacing: 0.01em;
  }
}
.modal__textarea::placeholder {
  color: rgba(117, 117, 117, 0.5);
}
.modal__textarea:focus, .modal__textarea:active {
  border: 2px solid var(--main-accent-color);
}
.modal__submit {
  width: 200px;
  height: 50px;
  margin: 0 auto;
  background-color: var(--main-accent-color);
  box-shadow: 0px 4px 4px rgba(255, 255, 255, 0.5);
  border-radius: 25px;
  color: var(--main-white-color);
  font-size: 16px;
  font-weight: 700;
  line-height: 1.87;
  letter-spacing: 0.06em;
}
.modal__submit:hover, .modal__submit:focus {
  background-color: var(--secondary-accent-color);
  box-shadow: 0px 15px 15px rgba(255, 255, 255, 0.8);
}
.modal__count-date-container {
  display: flex;
  flex-direction: column;
}
@media screen and (min-width: 480px) {
  .modal__count-date-container {
    flex-direction: row;
  }
}
@media screen and (min-width: 768px) {
  .modal__flex-count-date {
    margin-right: 37px;
  }
}

.btn-close {
  position: absolute;
  top: 0;
  right: -30px;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  border: 1px solid #fff;
  background-color: transparent;
  color: #fff;
  transition-duration: 0ms;
}
.btn-close:hover, .btn-close:focus {
  color: var(--background-color);
  border-color: var(--background-color);
}
.btn-close__icon {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 18px;
  height: 18px;
  transition-duration: 0ms;
}

sources.sass\\utils\\_common.scss:
body {
	font-family: 'DM Sans', sans-serif;
	color: var(--main-black-color);
	font-size: 16px;
	letter-spacing: 0.04em;
	background-color: var(--main-white-color);
}

h1,
h2,
h3,
h4,
h5,
h6,
p,
ul {
	padding: 0;
	margin: 0;
}
img {
	display: block;
	max-width: 100%;
	height: auto;
}
.visually-hidden {
	position: absolute;
	width: 1px;
	height: 1px;
	margin: -1px;
	border: 0;
	padding: 0;

	white-space: nowrap;
	clip-path: inset(100%);
	clip: rect(0 0 0 0);
	overflow: hidden;
}

.container {
	width: 100vw;
	padding-left: 20px;
	padding-right: 20px;
	margin: 0 auto;
	@include mob {
		width: 480px;
	}

	@include tab {
		width: 768px;
	}

	@include desk {
		width: 1200px;
	}
}
a {
	text-decoration: none;
	color: inherit;
	font-weight: inherit;
	font-size: inherit;
	line-height: inherit;
	letter-spacing: inherit;
}
ul {
	list-style-type: none;
	margin: 0;
	padding: 0;
}
button {
	padding: 0;
	font-family: inherit;
	cursor: pointer;
	background-color: transparent;
	border: none;
	outline: none;
}

sources.sass\\mixins\\_@media-rules.scss:
@mixin mob() {
	@media screen and (min-width: 480px) {
		@content;
	}
}
@mixin tab() {
	@media screen and (min-width: 768px) {
		@content;
	}
}
@mixin desk() {
	@media screen and (min-width: 1200px) {
		@content;
	}
}
@mixin x2() {
	@media (min-device-pixel-ratio: 2),
		(-webkit-min-device-pixel-ratio: 2),
		(min-resolution: 192dpi),
		(min-resolution: 2dppx) {
		@content;
	}
}

sources.sass\\components\\_hero-header.scss:
.header-container {
	position: relative;
	overflow: hidden;
	margin: 0 auto;
	height: 502px;
	padding: 6px 20px 0px 20px;
	@include tab {
		padding: 37px 34px 0px 34px;
		height: 399px;
	}
	@include desk {
		padding: 37px 74px 0px 74px;
		height: 665px;
	}
}

.hero-header {
	background-color: var(--background-color);
	width: 100%;
}

.page-nav {
	display: flex;
	justify-content: space-between;
	align-items: center;
	&__menu-btn {
		position: absolute;
		top: 25px;
		right: 20px;
		width: 20px;
		height: 12px;
		z-index: 2;
		@include tab {
			top: 52px;
			right: 224px;
		}
	}
	&__burger {
		width: 20px;
		height: 12px;
		vertical-align: top;
		@include desk {
			display: none;
		}
	}
	&__btn {
		display: none;
		width: 160px;
		height: 44px;
		background-color: var(--main-white-color);
		color: var(--main-accent-color);
		border-radius: 22px;
		transition-property: background-color;
		transition-duration: var(--primary-transition-duration);
		transition-timing-function: var(--primary-transition-timing-function);
		animation-name: BgColor;
		animation-duration: 2s;
		animation-delay: 4s;
		animation-iteration-count: 2;
		animation-timing-function: var(--primary-transition-timing-function);
		@include font(16px, 700, 1.31, 0.03em);
		@include tab {
			display: flex;
			align-items: center;
			padding-left: 37px;
			position: relative;
		}
		&::after {
			content: '\276F';
			font-size: 12px;
			position: absolute;
			top: 15px;
			right: 33px;
			color: var(--main-accent-color);
		}
	}
}

.page-nav__btn:focus,
.page-nav__btn:hover {
	background-color: var(--secondary-gray-color);
}

@keyframes BgColor {
	0% {
		background-color: var(--main-white-color);
	}

	50% {
		background-color: var(--non-accent-color);
	}

	100% {
		background-color: var(--main-white-color);
	}
}

.site-nav {
	display: none;
	@include desk {
		display: flex;
		justify-content: space-between;
		width: 541px;
		z-index: 2;
	}
}

.site-nav__item {
	color: var(--main-white-color);
	transition-property: background-color;
	transition-duration: var(--primary-transition-duration);
	transition-timing-function: var(--primary-transition-timing-function);
	@include font(16px, 700, 1.14, 0.04em);
	&:focus,
	&:hover {
		color: var(--main-accent-color);
	}
}

.site-nav__item--active {
	color: var(--main-accent-color);
}

.hero-container {
	position: absolute;
	top: 193px;
	left: 20px;
	z-index: 5;
	@include tab {
		top: 102px;
		left: 34px;
	}
	@include desk {
		top: 171px;
		left: 74px;
	}
}
.hero-container__title {
	font-family: Titan One, sans-serif;
	text-transform: uppercase;
	@include font(26px, 400, 1.15, 0.04em);
	color: var(--main-white-color);
	padding-bottom: 19px;
	@include tab {
		@include font(22px, 400, 1.14, 0.04em);
	}
	@include desk {
		@include font(38px, 400, 1.16, 0.04em);
		padding-bottom: 25px;
	}
}

.hero-container__title--accent {
	font-family: Titan One, sans-serif;
	text-transform: uppercase;
	@include font(48px, 400, 0.97, 0.02em);
	color: var(--main-accent-color);
	@include tab {
		@include font(34px, 400, 0.91, 0.02em);
	}
	@include desk {
		@include font(58px, 400, 0.9, 0.02em);
	}
}

.hero__btn {
	width: 83px;
	height: 24px;
	background-color: var(--main-white-color);
	color: var(--main-accent-color);
	border-radius: 20px;
	transition-property: background-color;
	transition-duration: var(--primary-transition-duration);
	transition-timing-function: var(--primary-transition-timing-function);
	@include font(8px, 400, 1.25, null);
	text-align: center;
	@include desk {
		width: 138px;
		height: 40px;
		@include font(14px, 400, 1.28, null);
	}
	&--accent {
		width: 83px;
		height: 24px;
		background-color: var(--main-accent-color);
		color: var(--main-white-color);
		border-radius: 20px;
		transition-property: background-color;
		transition-duration: var(--primary-transition-duration);
		transition-timing-function: var(--primary-transition-timing-function);
		@include font(8px, 400, 1.25, null);
		text-align: center;
		margin-right: 8px;
		@include desk {
			width: 102px;
			height: 40px;
			@include font(14px, 400, 1.28, null);
			margin-right: 15px;
		}
	}
}

.hero__btn:focus,
.hero__btn:hover {
	background-color: var(--secondary-gray-color);
}
.hero__btn--accent:focus,
.hero__btn--accent:hover {
	background-color: var(--secondary-accent-color);
}

.hero-picture {
	position: absolute;
	right: 32px;
	bottom: 0;
	z-index: 3;
	animation-name: scale;
	animation-duration: 2s;
	animation-delay: 500ms;
	animation-iteration-count: 2;
	animation-timing-function: var(--primary-transition-timing-function);
	@include tab {
		right: 247px;
	}
	@include desk {
		right: 410px;
	}
}

@keyframes scale {
	0% {
		transform: scale(1);
	}

	50% {
		transform: scale(1.15);
	}

	100% {
		transform: scale(1);
	}
}

.hero-picture-girl {
	display: none;
	@include tab {
		display: block;
		position: absolute;
		right: 34px;
		bottom: 0;
		z-index: 4;
	}
	@include desk {
		right: 74px;
	}
}

.hero-picture-milk {
	display: none;
	@include tab {
		display: block;
		position: absolute;
		left: 34px;
		bottom: 28px;
	}
	@include desk {
		left: 74px;
		bottom: 45px;
	}
}

.ellipse {
	position: absolute;
	right: -62px;
	bottom: 75px;
	background-color: var(--title-label);
	width: 346px;
	height: 344px;
	border-radius: 50%;
	@include tab {
		right: 139px;
		bottom: 45px;
		width: 322px;
		height: 322px;
	}
	@include desk {
		right: 227px;
		bottom: 75px;
		width: 538px;
		height: 538px;
	}
}

.hero-inform__16 {
	display: flex;
	flex-direction: column;
	align-items: flex-end;
	position: absolute;
	top: 96px;
	right: 44px;
	@include desk {
		top: 160px;
		right: 92px;
	}
	&::after {
		content: '';
		position: absolute;
		top: -2px;
		right: -10px;
		width: 16px;
		height: 16px;
		border-radius: 50%;
		background-color: var(--main-white-color);
		@include desk {
			top: -4px;
			right: -18px;
			width: 30px;
			height: 30px;
		}
	}
}

.hero-inform {
	display: none;
	@include tab {
		display: block;
	}
}

.hero-inform__23 {
	display: flex;
	flex-direction: column;
	align-items: flex-end;
	position: absolute;
