sources.sass\\components\\_advantages.scss:
.advantages {
	background-color: var(--background-color-pink);
	padding-bottom: 97px;

	@include tab {
		padding-bottom: 140px;
	}
	@include desk {
		padding-bottom: 139px;
	}

	&-list {
		@include tab {
			display: flex;
			margin: -15px;
			flex-wrap: wrap;
		}
		&__item {
			margin-bottom: 38px;
			@include tab {
				flex-basis: calc((100% - 3 * var(--card-set-gap)) / 3);
				margin: 15px;
			}
			&::before {
				content: '';
				background-color: var(--background-color-pink);
				height: 50px;

				display: block;
				background-repeat: no-repeat;
				background-size: contain;
				background-position: center;

				margin-bottom: 16px;
			}
			&:nth-child(1)::before {
				background-image: url('../images/advantages-icon1-mob@1x.png');
				width: 33px;
				@media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
					background-image: url('../images/advantages-icon1-mob@2x.png');
				}
			}

			&:nth-child(2)::before {
				background-image: url('../images/advantages-icon2-mob@1x.png');
				width: 50px;
				@media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
					background-image: url('../images/advantages-icon2-mob@2x.png');
				}
			}

			&:nth-child(3)::before {
				background-image: url('../images/advantages-icon3-mob@1x.png');
				width: 44px;
				@media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
					background-image: url('../images/advantages-icon3-mob@2x.png');
				}
			}
		}
	}
	&-span {
		@include font(36px, 400, 1.14, 0.04em);
		font-family: Titan One;
		color: var(--main-accent-color);
		text-transform: uppercase;
		height: 56px;
		display: block;
		@include tab {
			@include font(48px, 400, 1.15, 0.04em);
			margin-bottom: 13px;
		}
		@include desk {
			@include font(58px, 400, 1.14, 0.04em);
			height: 65px;
			margin-bottom: 15px;
		}
	}
	&-text {
		@include font(12px, 700, 1.7, 0.02em);
		color: var(--main-black-color);
		max-width: 230px;
		@include tab {
			@include font(14px, 700, 1.74, 0.02em);
			max-width: 180px;
		}
		@include desk {
			@include font(16px, 700, 1.76, 0.02em);
			max-width: 330px;
		}
	}
}

sources.sass\\components\\_gallery.scss:
.section.gallery {
  background-image: linear-gradient(to bottom, var(	--background-color-pink) 0%, var(	--background-color-pink) 40%, var(--main-white-color) 40%, var(--main-white-color) 100%);
}

.gallery__container {
    display: flex;
    justify-content: center;  
}

.gallery__image {
  display: block;
  transition: all 250ms cubic-bezier(0.075, 0.82, 0.165, 1);
}

.gallery__column {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  &:first-child,
    &:last-child {
      align-items: center;
      justify-content: center;
    }
}

.gallery__column:nth-child(2n + 3) {
  transform: translateY(-10%);
}

.gallery__column:nth-last-child(2n + 3) {
  transform: translateY(10%);
}

.gallery__image-wrapper {
  border-radius: 18%;
  margin: 3px;
  overflow: hidden;
  transition: all 250ms cubic-bezier(0.075, 0.82, 0.165, 1);
}

.gallery__image-wrapper:hover {
  @include mob {
		width: 80px;
    height: 80px;
	}

	@include tab {
		width: 170px;
    height: 170px;
	}

	@include desk {
		width: 280px;
    height: 280px;
	}

  & .gallery__image {
    transform: scale(2);
    transform-origin: top left;
  }
}

sources.sass\\components\\_customer-reviews.scss:
.customer-reviews {
	padding-bottom: 86px;
	padding-top: 80px;
	@include tab {
		padding-bottom: 119px;
		padding-top: 121px;
	}
	@include desk {
		padding-bottom: 132px;
		padding-top: 123px;
	}
	margin-left: auto;
	margin-right: auto;
}

.slider {
	&__img {
		margin-bottom: 29px;
		@include tab {
			margin-bottom: 19px;
		}
		@include desk {
			margin-bottom: 15px;
		}
		margin-left: auto;
		margin-right: auto;
	}
	&__text-box {
		position: relative;
		background-color: var(--slider-background);

		width: 100%;
		padding: 48px 50px 20px 50px;
		margin-left: auto;
		margin-right: auto;
		margin-bottom: 30px;
		@include tab {
			padding: 47px 36px 28px 60px;
			margin-bottom: 24px;
			max-width: 620px;
		}
		@include desk {
			padding: 47px 54px 32px 59px;
			margin-bottom: 15px;
			max-width: 926px;
		}

		&::before {
			content: '';
			background-image: url(../images/double-quote.svg);
			position: absolute;
			top: 28px;
			left: 61px;
			@include tab {
				top: 30px;
				left: 40px;
			}
			@include desk {
				top: 32px;
				left: 50px;
			}
			width: 54px;
			height: 40px;
			fill: var(--double-quote);

			z-index: 1;
		}
	}

	&__text {
		position: relative;

		font-family: 'DM Sans', sans-serif;
		font-weight: 500;
		font-size: 12px;
		line-height: calc(30.6 / 12);
		text-align: center;

		color: var(--main-black-color);

		z-index: 2;
		@include tab {
			font-size: 14px;
			line-height: calc(30.24 / 14);
		}
		@include desk {
			font-size: 16px;
			line-height: calc(29.76 / 16);
		}
	}
	&__underline {
		padding-left: 9px;
		padding-right: 14px;

		background: linear-gradient(transparent 30px, var(--double-quote) 30px);
		background-size: 100% 31px;
		border-bottom: 1px solid var(--double-quote);
		@include tab {
			padding-left: 20px;
			padding-right: 44px;
			background: linear-gradient(transparent 29px, var(--double-quote) 29px);
			background-size: 100% 30px;
			border-bottom: none;
		}
		@include desk {
			padding-left: 28px;
			padding-right: 35px;
		}
	}
	&__customer-name {
		font-weight: 500;
		font-size: 14px;
		line-height: 186%;
		text-align: center;
		color: var(--main-black-color);

		margin-bottom: 30px;
		@include tab {
			margin-bottom: 35px;
			font-size: 16px;
		}
		@include desk {
			font-size: 20px;
		}
		&::before {
			content: '';
			background-image: url(../images/3dots.svg);
			width: 32px;
			height: 4px;
			display: block;
			margin-left: auto;
			margin-right: auto;
			margin-bottom: 18px;
			@include tab {
				margin-bottom: 14px;
			}
		}
	}
}
.slick-dots li button:before {
	width: 12px;
	height: 12px;
	content: '';
	background-image: url(../images/gray-ellipse.svg);
	background-repeat: no-repeat;
	background-size: cover;
}
.slick-dots li.slick-active button:before {
	background-image: url(../images/home.svg);
	background-repeat: no-repeat;
	background-size: cover;
}

